// Get the incoming data - handle both parsed and raw body
const inputData = $input.first().json;

let body;

// Check if body is already parsed (object) or needs parsing (string)
if (inputData.body) {
  // Body exists as a property
  if (typeof inputData.body === 'string') {
    try {
      body = JSON.parse(inputData.body);
    } catch (e) {
      throw new Error(`Failed to parse body string: ${e.message}`);
    }
  } else {
    // Body is already an object
    body = inputData.body;
  }
} else {
  // The input itself is the body (n8n auto-parsed it)
  body = inputData;
}

const slideCount = body.slideCount || 3;
const sources = body.sources || [];
const language = body.options?.language || 'en';

// Extract content descriptions and prepare for the agent
let contentDescriptions = [];
let base64Images = [];

for (const source of sources) {
  if (source.type === 'application/pdf') {
    contentDescriptions.push(`[PDF Document: ${source.name}]`);
    base64Images.push({
      name: source.name,
      type: 'pdf',
      data: source.data
    });
  } else if (source.type && source.type.startsWith('image/')) {
    contentDescriptions.push(`[Image: ${source.name}]`);
    base64Images.push({
      name: source.name,
      type: 'image',
      mimeType: source.type,
      data: source.data
    });
  }
}

// Build the prompt for the AI Agent
const agentPrompt = `You are an expert social media content creator for GF Innovative Solutions, a German technology consultancy.

I need you to analyze the provided content and create Instagram carousel text.

**Content Sources:**
${contentDescriptions.length > 0 ? contentDescriptions.join('\n') : 'No specific sources provided - create general tech industry content.'}

**Requirements:**
- Create exactly ${slideCount} slides
- Generate THREE different versions with different tones:
  1. Professional (formal, corporate, B2B focused)
  2. Engaging (dynamic, enthusiastic, with occasional emojis)
  3. Concise (short, punchy, impactful)

**Slide Guidelines:**
- Each slide: 1-3 sentences max
- Slide 1: Hook the reader
- Middle slides: Key information
- Last slide: Call-to-action

**CRITICAL: Respond with ONLY this JSON structure, no markdown, no explanation:**
{
  "options": [
    {
      "tone": "professional",
      "label": "Professional",
      "description": "Formal and authoritative tone",
      "slides": [
        {"slideNumber": 1, "text": "..."},
        {"slideNumber": 2, "text": "..."}
      ]
    },
    {
      "tone": "engaging",
      "label": "Engaging", 
      "description": "Dynamic and captivating tone",
      "slides": [
        {"slideNumber": 1, "text": "..."},
        {"slideNumber": 2, "text": "..."}
      ]
    },
    {
      "tone": "concise",
      "label": "Concise",
      "description": "Short and punchy tone",
      "slides": [
        {"slideNumber": 1, "text": "..."},
        {"slideNumber": 2, "text": "..."}
      ]
    }
  ]
}`;

return {
  json: {
    slideCount,
    language,
    agentPrompt,
    base64Images,
    sourcesCount: sources.length,
    contentDescriptions
  }
};

// Prepare messages for OpenAI with vision support
const input = $input.first().json;
const base64Images = input.base64Images || [];
const agentPrompt = input.agentPrompt;

// Build content array for GPT-4 Vision
let messageContent = [];

// Add images first (GPT-4 Vision format)
for (const item of base64Images) {
  if (item.type === 'image' && item.data) {
    messageContent.push({
      type: 'image_url',
      image_url: {
        url: item.data,
        detail: 'high'
      }
    });
  }
  // For PDFs - add as text description since GPT-4 can't read PDFs directly
  if (item.type === 'pdf') {
    messageContent.push({
      type: 'text',
      text: `[Attached PDF: ${item.name}]`
    });
  }
}

// Add the main text prompt
messageContent.push({
  type: 'text',
  text: agentPrompt
});

return {
  json: {
    messageContent,
    hasImages: base64Images.some(img => img.type === 'image'),
    hasPdfs: base64Images.some(img => img.type === 'pdf'),
    slideCount: input.slideCount,
    promptText: agentPrompt
  }
};

// Parse the AI Agent's response
const response = $input.first().json;

let outputText = '';

// Extract text from various response formats
if (response.output) {
  outputText = typeof response.output === 'string' ? response.output : JSON.stringify(response.output);
} else if (response.text) {
  outputText = response.text;
} else if (response.message?.content) {
  outputText = response.message.content;
} else if (typeof response === 'string') {
  outputText = response;
} else {
  // Try to find any string property containing options
  for (const key of Object.keys(response)) {
    const val = response[key];
    if (typeof val === 'string' && val.includes('options')) {
      outputText = val;
      break;
    }
  }
  // If still empty, stringify the whole response
  if (!outputText) {
    outputText = JSON.stringify(response);
  }
}

// Clean up the response - remove markdown code blocks if present
outputText = outputText
  .replace(/```json\s*/gi, '')
  .replace(/```\s*/g, '')
  .trim();

// Try to extract JSON if wrapped in other text
const jsonMatch = outputText.match(/\{[\s\S]*"options"\s*:\s*\[[\s\S]*\]\s*\}/);
if (jsonMatch) {
  outputText = jsonMatch[0];
}

try {
  const parsed = JSON.parse(outputText);

  // Validate structure
  if (!parsed.options || !Array.isArray(parsed.options)) {
    throw new Error('Invalid response: missing options array');
  }

  if (parsed.options.length !== 3) {
    throw new Error(`Invalid response: expected 3 options, got ${parsed.options.length}`);
  }

  // Validate each option
  for (let i = 0; i < parsed.options.length; i++) {
    const option = parsed.options[i];
    if (!option.slides || !Array.isArray(option.slides)) {
      throw new Error(`Option ${i + 1} missing slides array`);
    }
    if (!option.tone || !option.label) {
      throw new Error(`Option ${i + 1} missing tone or label`);
    }
  }

  return {
    json: parsed
  };

} catch (error) {
  console.error('Parse error:', error.message);
  console.error('Raw output:', outputText.substring(0, 500));

  // Return error response
  return {
    json: {
      error: true,
      message: `Failed to parse AI response: ${error.message}`,
      rawResponse: outputText.substring(0, 1000)
    }
  };
}
